#include "config.h"
#include "firmware.h"
#include "app.h"

extern volatile uint8_t count_flag;

int main(void)
{
	FW_InitClocksAndGPIO();
	FW_TIM1_InitPWM();
	FW_TIM2_Init();
	FW_TIM3_InitBridge();
	FW_TIM7_Init();

	APP_Init();

	// サンプル位相：周期中央
	FW_SetSampleMarker((uint16_t) (TIM1_ARR / 2));

	FW_StartAll();

	while (1)
	{
		if(count_flag == 1)
		{
			count_flag = 0;
			APP_Step();
		}
	}
}
